==/UserStyle== */
@-moz-document domain("zhihu.com") {
/* 全局 */

/* ================== */
/* ====== 字体 ====== */
/* ================== */

p {
	line-height: 1.8em;
	letter-spacing: .2px
}




/* =================== */
/* ====== 毛玻璃 ====== */
/* =================== */

/*[[backdrop-filter]]*/




/* ================== */
/* ====== 去除广告 ====== */
/* ================== */

/*[[ad]]*/




/* ===================== */
/* ====== 全局控件 ====== */
/* ===================== */

/* 圆形头像 & 圆形图标 */
.Avatar {
	border-radius: 50%
}
/* 圆形头像 & 圆形图标 */


/* Card 圆角 */
.Card {
    border-radius: 6px
}
/* Card 圆角 */


/* Modal-inner */
.Modal-inner {
	border-radius: 6px
}
/* Modal-inner */


/* 全局 svg 按钮 */
.Button--plain {
	color: rgba(9,9,9,.5)
}

.Button--plain:hover {
	color: rgba(9,9,9,.6)
}

.Button--plain .Button-zi {
	color: rgba(9,9,9,.4)
}

.Button--plain:hover .Button-zi {
	color: rgba(9,9,9,.5)
}

/* 申请授权 & 禁止转载 */
a.Menu-item.AnswerItem-selfMenuItem {
	color: rgba(9,9,9,.5)
}

a.Menu-item.AnswerItem-selfMenuItem:hover {
	color: rgba(9,9,9,.6)
}
/* 申请授权 & 禁止转载 */
/* 全局 svg 按钮 */


/* ====== 排序 ====== */
.Popover .Select-plainButton {
	background: none;
}

.Select-option {
	color: rgba(9,9,9,.5)
}

.Select-option:hover {
	color: rgba(9,9,9,.6)
}
/* ====== 排序 ====== */




/* ===================== */
/* ====== 鼠标移上表情的动效 ====== */
/* ===================== */
.StickerPopover-stickers-item:hover {
	cursor: pointer
}

.StickerPopover-stickers-item img {
	transition: transform .3s
}

.StickerPopover-stickers-item:hover img {
	transform: scale(1.3);
	transition: transform .3s
}




/* =================== */
/* ====== 导航栏 ====== */
/* =================== */

/* ====== 首页 & 发现 & 等你来答 ====== */
.AppHeader-Tabs {
	margin: 0 28px
}

.AppHeader-TabsLink.is-active {
	color: #333
}

.AppHeader-TabsLink {
	color: #888
}

.AppHeader-TabsLink:hover {
	color: #333
}
/* ====== 首页 & 发现 & 等你来答 ====== */


/* ====== 搜索栏 ====== */
/* 搜索框 */
.SearchBar {
	margin: 0 25px
}

.SearchBar-tool .Input-wrapper {
	border: 1px solid rgba(9,9,9,.1)
}

.SearchBar-tool .Input-wrapper.is-focus {
	border: 1px solid rgba(9,9,9,.14)
}

.Input-wrapper.Input-wrapper--grey {
	background: none
}

.Zi.Zi--Search {
	fill: rgba(9,9,9,.14)
}

.SearchBar-searchButton.Button--primary.Button--blue {
	background-color: transparent
}

.SearchBar-searchButton.Button--primary.Button--blue:hover {
	border-color: transparent
}

.SimpleSearchBar-solidSearchButton.Button--primary.Button--blue {
	background-color: transparent;
	border-color: transparent
}

.AppHeader .Popover input.Input {
	color: #444
}

.AppHeader .Popover input.Input::-webkit-input-placeholder {
	color: rgba(9,9,9,.3)
}

.AppHeader .Popover .SearchBar-focusedInput input.Input::-webkit-input-placeholder {
   	color: rgba(9,9,9,.4)
}
/* 搜索框 */

/* 提问按钮 */
.SearchBar-askButton {
	margin-left: 15px
}
/* 提问按钮 */
/* ====== 搜索栏 ====== */


/* ====== 搜索栏列表 ====== */
.SearchBar-menu {
	padding-top: 0
}

.SearchBar-label {
	height: 15px;
	padding-top: 14px;
	padding-bottom: 14px;
	font-size: 15px;
	font-weight: bold;
	color: red;
	text-align: center;
	line-height: 15px;
	border-bottom-color: rgba(9,9,9,.1)
}

.Menu-item {
	color: #333;
