/* ==UserStyle==
@name           QQ邮箱 UI优化版
@namespace      USO Archive
@author         tvrcgo
@description    `QQ邮箱个人版界面优化- 增大邮件列表间隔- Gmail风格按钮- 去广告- 去掉每日推荐- 圆角菜单`
@version        20121203.23.37
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain('mail.qq.com') {

table.i td.cx, table.i td.ci, table.i td.l {padding-top:7px;padding-bottom:7px;font:normal 1em tohama,arial,sans-serif;}

li.mailinfo1 a {text-decoration:none;}

.toolheight input.btn {padding:4px 7px;}

div.tipbook-top, div#panelDy, div#dyTab { display:none; }

div#panelPersonInfo { display:block; }

/* 左栏菜单 */
.newskinbody li.fs a:hover { -webkit-border-radius:4px; }
div#navMidBar li.fn, div#navMidBar li.fs { padding:3px 0;-webkit-border-radius:2px; }
div#navMidBar li.fn a:hover, div#navMidBar li.fs a:hover { padding:3px auto;-webkit-border-radius:2px; }

