}

.top_cont_toggle .toggleBtn:after {
	content: "\e316";
	font-family: "Material Icons";
	font-size: 20px;
	display: inline-block;
	vertical-align: top;
	text-indent: 0;
	-moz-transform: translateY(0.125em);
}

.top_content_closed .toggleBtn:before {
