.sub_nav[id*="粉丝"]+#follow .user:not([is_concerned="1"]) .right>div:before{
	content: "\e148";
}
.sub_nav[id*="粉丝"]+#follow .user:not([is_concerned="1"]) .right>div:after{
	content:"关注TA";
}

.sub_nav[id*="关注"]+#follow .user .right>div:before{
	content: "\e888";
}
.sub_nav[id*="关注"]+#follow .user .right>div:after{
	content:"取消关注";
}
#follow .list #pagerPanel{
	margin: 20px auto 0;
}
.w978 .sub_nav{
	display: block;
	height: 30px;
	line-height: 30px;
	font-size: 14px;
	color: var(--m-light-40);
	font-weight: bold;
	margin-bottom:10px !important;
	padding: 0 10px;
	background: var(--m-block-d004);
	border-radius: 6px;
}
.w978 .sub_nav[id*="关注"]:before{
	content: "我关注的用户"
}
.w978 .sub_nav[id*="粉丝"]:before{
	content: "我的粉丝"
}
/*个人资料设置*/
#user{
	background: none !important;
}

.op-pic h4{
	color: var(--m-light-20);
	border-bottom: 1px solid rgba(0,0,0,calc(0.1*var(--m-line-darken)));
}

.op-pic .current-pic{
	background: rgba(0,0,0,calc(0.04*var(--m-block-darken)));
	width: 110px;
	height: 110px;
}

#bt_save_disabled,
#btn_cancel{
	color: #fff;
	background: var(--m-ecolor);
	border: none;
	border-radius: 4px;
	cursor: pointer;
	transition: background .4s ease;
}
#bt_save_disabled:hover,
#btn_cancel:hover{
	background: var(--m-ehcolor);
}
#btn_cancel{
	display: none !important;
}

/*签名档设置*/
#panel_sign{
	width: 100%;
}
.mysign{
	color: var(--m-light-30);
}
#sign_add{
	float: right;
	margin-left: 20px;
	padding: 0 10px;
	display: inline-block;
	color: #fff;
	background: var(--m-ecolor);
	border: none;
	border-radius: 4px;
	cursor: pointer;
	transition: background .4s ease;
}
#sign_add:hover{
	background: var(--m-ehcolor);
}
#sign_add:before{
	content: "\e148";
	font-size: 16px;
	font-family: "Material Icons";
	display: inline-block;
	vertical-align: bottom;
	margin-right: 4px;
	-moz-transform: translateY(0.125em);
}
.signlist{
	margin: 0;
	padding: 10px;
	display: flex;
	flex-wrap: wrap;
	margin-right: -20px;
}
.module_sign{
	margin: 0 20px 20px 0;
}

.sign_name{
	background: var(--m-block-b004);
	border-radius: 6px;
	line-height:30px;
	margin-bottom: 5px;
	padding: 0 5px 0 10px;
	display: flex;
	align-items: center;
}
.sign_name em{
	flex: 1;
}

#sign_modify,#sign_del{
	display: inline-block;
	line-height: 18px;
	padding: 0 10px;
	color: var(--m-clight-40);
	background: rgba(0,0,0,.08);
	margin-left: 5px;
	font-weight: normal;
	transition-property: background, box-shadow;
	transition-duration: .4s;
	transition-timing-function: ease;
}
#sign_modify:hover,#sign_del:hover{
	background: rgba(255,255,255,.04);
	box-shadow: 0 1px 4px 2px rgba(0,0,0,.1);
}

.sign_tip{
	color: var(--light-20);
}
.sign_img{
	margin: 0;
	margin-top: 5px;
}
.sign_name img,
.signlist hr,
.sign_line{
	display: none !important;
}
#preview{
	border:1px solid rgba(0,0,0,calc(0.1*var(--m-line-darken)));
}

