}

.replace_tip:hover {
	background-color: rgba(0,0,0,.4) !important;
}

.replace_tip:before {
	content: "\e5cf";
	font-family: "Material Icons";
	display: inline-block;
	vertical-align: top;
	font-size: 28px;
