	pointer-events: none;
	transition-property: opacity;
	transition-duration: .5s;
	transition-timing-function: ease;
}

.j_jb_ele:hover .super_jubao {
	opacity: 1;
	pointer-events: auto;
}

.p_post_del_my,
.p_post_del,
.p_post_ban {
