	color: var(--m-light-72) !important;
	font-size: 20px;
	cursor: pointer;
	transition-property: color, opacity;
	transition-duration: .5s;
	transition-timing-function: ease;
}

.user-hide-post-down[style*="none"] {
	opacity: 0;
	pointer-events: none;
}

.user-hide-post-down:hover,
.user-hide-post-up:hover {
