	margin-left: 20px;
	padding: 0 10px;
	display: inline-block;
	color: #fff;
	background: var(--m-ecolor);
	border: none;
	border-radius: 4px;
	cursor: pointer;
	transition: background .4s ease;
}
#sign_add:hover{
	background: var(--m-ehcolor);
}
#sign_add:before{
