#gb > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(1) > a, #gb > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(3) > div:nth-child(1) > a, #gbw > div > div > div > div > div > a > div, #gbw > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(2) > div:nth-child(1) > a {
	filter: invert(.9)!important;
}
/* notifications main page */
#gb > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1), #gbsfw {
	border-color: #333!important;
}
/* blue account button */
#gb > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(4) > div:nth-child(2) > div:nth-child(1) > div:nth-child(2) > a, #gbw > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div > div:nth-child(2) > div:nth-child(1) > div > a, #gb > div:nth-child(2) > div:nth-child(6) > div:nth-child(1) > div > a {
	color: #fff!important;
	cursor: pointer!important;
}
/* accounts main page */
#gb > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2) > div, #gbw > div > div > div:nth-child(2) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2) > div {
	background: #252525!important;
}
/* accounts hover main page */
#gb > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2) > div > a:hover, #gbw > div > div > div:nth-child(2) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2) > div > a:hover {
	background: #333!important;
}
/* Account name */
#gb > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2) > div > a > div > div:nth-child(1), #gbw > div > div > div:nth-child(2) > div:nth-child(4) > div:nth-child(2) > div:nth-child(2) > div > a > div > div:nth-child(1), #gb > div > div > div:nth-child(2) > div:nth-child(2) > div:nth-child(2) > div > a > div > div:nth-child(1) {
	color: #ccc!important;
}
/* add account & sign out button area */
#gb > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(4) > div:nth-child(2) > div:nth-child(4), #gbw > div > div > div:nth-child(2) > div:nth-child(4) > div:nth-child(2) > div:nth-child(4)  {
	background: #252525!important;
}
/* images add a shortcut text */
#gbwa > div:nth-child(2) > div > div:nth-child(2) > div {
	color: #ccc!important;
}
/* google page name */
#gb > div:nth-child(2) > div:nth-child(1) > div:nth-child(4) > div > a > span {
	color: #fff!important;
}
/* FF Scrollbar */
* {
	scrollbar-width: thin!important;
	scrollbar-color: #333 #1b1b1b!important;
}
}

